#pragma once
#include <Arduino.h>
#include <Servo.h>
#include <ESP8266WiFi.h>
#include <DNSServer.h>
#include <ESPAsyncWebServer.h>

// 舵机控制类封装
class DogServo {
private:
  Servo servo;
  int pin;
  bool active;
  int minAngle;
  int maxAngle;
  
public:
  DogServo(int pin, int minAngle, int maxAngle) 
    : servo(),
      pin(pin),
      active(false),
      minAngle(minAngle),
      maxAngle(maxAngle)
  {}
    
  void setup();                                         // 初始化
  void setAngle(int angle);                             //  设置角度
  void start();                                         //  启动
  void stop();                                          //  停止
  bool isActive() const;                                //  是否激活
  void swing(int fromAngle, int toAngle, int duration); //  摇摆
};

